import{_ as i,c as a,o as r,ae as o}from"./chunks/framework.BHrE6nLq.js";const c=JSON.parse('{"title":"HRT 用药指南","description":"","frontmatter":{},"headers":[],"relativePath":"medical/hrt/guide.md","filePath":"medical/hrt/guide.md","lastUpdated":null}'),t={name:"medical/hrt/guide.md"};function e(s,l,n,u,h,g){return r(),a("div",null,l[0]||(l[0]=[o('<h1 id="hrt-用药指南" tabindex="-1">HRT 用药指南 <a class="header-anchor" href="#hrt-用药指南" aria-label="Permalink to &quot;HRT 用药指南&quot;">​</a></h1><div class="danger custom-block"><p class="custom-block-title">警告</p><p>本文内容仅供参考。请勿在没有专业医生指导的情况下自行使用任何药物。</p></div><h2 id="雌激素类药物" tabindex="-1">雌激素类药物 <a class="header-anchor" href="#雌激素类药物" aria-label="Permalink to &quot;雌激素类药物&quot;">​</a></h2><h3 id="戊酸雌二醇片剂" tabindex="-1">戊酸雌二醇片剂 <a class="header-anchor" href="#戊酸雌二醇片剂" aria-label="Permalink to &quot;戊酸雌二醇片剂&quot;">​</a></h3><ul><li><strong>通用名</strong>: 戊酸雌二醇片</li><li><strong>常见品牌</strong>: <ul><li>补佳乐（Progynova）</li><li>芬吗通（Femoston）</li></ul></li><li><strong>规格</strong>: 1mg/片，2mg/片</li><li><strong>使用方式</strong>: 口服</li><li><strong>注意事项</strong>: <ul><li>建议舌下含服以提高吸收率</li><li>可分次服用以保持稳定血药浓度</li><li>避免与食物同服</li><li>储存在阴凉干燥处</li></ul></li></ul><h3 id="雌二醇凝胶" tabindex="-1">雌二醇凝胶 <a class="header-anchor" href="#雌二醇凝胶" aria-label="Permalink to &quot;雌二醇凝胶&quot;">​</a></h3><ul><li><strong>通用名</strong>: 雌二醇凝胶</li><li><strong>常见品牌</strong>: <ul><li>爱斯妥（Oestrogel）</li><li>芬吗通（Femoston）凝胶</li></ul></li><li><strong>规格</strong>: 0.06%，每泵约 0.75mg 雌二醇</li><li><strong>使用方式</strong>: <ul><li>外用，涂抹在皮肤上</li><li>常用部位：大腿内侧、手臂内侧</li></ul></li><li><strong>注意事项</strong>: <ul><li>涂抹后等待完全吸收</li><li>避免涂抹部位接触水</li><li>避免与他人皮肤接触</li><li>每天固定时间使用</li></ul></li></ul><h3 id="雌二醇贴片" tabindex="-1">雌二醇贴片 <a class="header-anchor" href="#雌二醇贴片" aria-label="Permalink to &quot;雌二醇贴片&quot;">​</a></h3><ul><li><strong>通用名</strong>: 雌二醇贴片</li><li><strong>常见品牌</strong>: <ul><li>克龄蒙（Climara）</li><li>雌二醇贴剂</li></ul></li><li><strong>规格</strong>: 25μg/24h, 50μg/24h, 100μg/24h</li><li><strong>使用方式</strong>: <ul><li>贴于皮肤，每周更换1-2次</li><li>常用部位：腹部下方、臀部</li></ul></li><li><strong>注意事项</strong>: <ul><li>轮换贴敷部位</li><li>保持皮肤清洁干燥</li><li>避免贴在乳房部位</li><li>运动或洗澡时注意防水</li></ul></li></ul><h2 id="抗雄激素类药物" tabindex="-1">抗雄激素类药物 <a class="header-anchor" href="#抗雄激素类药物" aria-label="Permalink to &quot;抗雄激素类药物&quot;">​</a></h2><h3 id="醋酸环丙孕酮" tabindex="-1">醋酸环丙孕酮 <a class="header-anchor" href="#醋酸环丙孕酮" aria-label="Permalink to &quot;醋酸环丙孕酮&quot;">​</a></h3><ul><li><strong>通用名</strong>: 醋酸环丙孕酮</li><li><strong>常见品牌</strong>: <ul><li>色普龙（Androcur）</li><li>华典</li></ul></li><li><strong>规格</strong>: 50mg/片</li><li><strong>使用方式</strong>: 口服</li><li><strong>注意事项</strong>: <ul><li>建议从低剂量开始</li><li>需要定期检查肝功能</li><li>可能影响情绪和性欲</li><li>长期使用需监测泌乳素</li></ul></li></ul><h3 id="螺内酯" tabindex="-1">螺内酯 <a class="header-anchor" href="#螺内酯" aria-label="Permalink to &quot;螺内酯&quot;">​</a></h3><ul><li><strong>通用名</strong>: 螺内酯</li><li><strong>常见品牌</strong>: <ul><li>安体舒通</li><li>螺内酯片</li></ul></li><li><strong>规格</strong>: 20mg/片, 25mg/片</li><li><strong>使用方式</strong>: 口服</li><li><strong>注意事项</strong>: <ul><li>可能导致频繁排尿</li><li>需要监测血钾水平</li><li>避免补充过多钾盐</li><li>可能导致脱水</li></ul></li></ul><h2 id="用药建议" tabindex="-1">用药建议 <a class="header-anchor" href="#用药建议" aria-label="Permalink to &quot;用药建议&quot;">​</a></h2><h3 id="基本原则" tabindex="-1">基本原则 <a class="header-anchor" href="#基本原则" aria-label="Permalink to &quot;基本原则&quot;">​</a></h3><ol><li><p><strong>循序渐进</strong></p><ul><li>从低剂量开始</li><li>根据效果和检查结果调整</li><li>保持耐心，避免操之过急</li></ul></li><li><p><strong>规律用药</strong></p><ul><li>固定时间服用</li><li>不要随意调整剂量</li><li>记录用药情况</li><li>保存检查报告</li></ul></li><li><p><strong>定期监测</strong></p><ul><li>激素水平</li><li>肝肾功能</li><li>血脂</li><li>电解质</li></ul></li></ol><h3 id="常见剂量参考" tabindex="-1">常见剂量参考 <a class="header-anchor" href="#常见剂量参考" aria-label="Permalink to &quot;常见剂量参考&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">提示</p><p>以下剂量仅供参考，实际用药请遵医嘱。</p></div><ol><li><p><strong>雌二醇</strong></p><ul><li>起始剂量：1-2mg/天</li><li>维持剂量：4-8mg/天</li><li>分次服用：2-3次/天</li></ul></li><li><p><strong>色普龙</strong></p><ul><li>起始剂量：25mg/天</li><li>维持剂量：50-100mg/天</li><li>建议分次服用</li></ul></li><li><p><strong>螺内酯</strong></p><ul><li>起始剂量：50mg/天</li><li>维持剂量：100-200mg/天</li><li>建议分次服用</li></ul></li></ol><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h2><h3 id="服药禁忌" tabindex="-1">服药禁忌 <a class="header-anchor" href="#服药禁忌" aria-label="Permalink to &quot;服药禁忌&quot;">​</a></h3><ol><li><p><strong>绝对禁忌</strong></p><ul><li>严重肝病</li><li>血栓性疾病</li><li>激素依赖性肿瘤</li><li>未控制的精神疾病</li></ul></li><li><p><strong>相对禁忌</strong></p><ul><li>高血压</li><li>糖尿病</li><li>心脏病</li><li>肾功能不全</li></ul></li></ol><h3 id="用药安全" tabindex="-1">用药安全 <a class="header-anchor" href="#用药安全" aria-label="Permalink to &quot;用药安全&quot;">​</a></h3><ol><li><p><strong>储存要求</strong></p><ul><li>避光保存</li><li>常温干燥</li><li>远离儿童</li><li>注意有效期</li></ul></li><li><p><strong>用药记录</strong></p><ul><li>记录服用时间</li><li>记录剂量变化</li><li>记录不良反应</li><li>保存处方和检查单</li></ul></li><li><p><strong>应急预案</strong></p><ul><li>准备备用药物</li><li>记录医生联系方式</li><li>了解就医流程</li><li>保存病历资料</li></ul></li></ol><h2 id="副作用管理" tabindex="-1">副作用管理 <a class="header-anchor" href="#副作用管理" aria-label="Permalink to &quot;副作用管理&quot;">​</a></h2><h3 id="常见副作用" tabindex="-1">常见副作用 <a class="header-anchor" href="#常见副作用" aria-label="Permalink to &quot;常见副作用&quot;">​</a></h3><ol><li><p><strong>雌激素相关</strong></p><ul><li>乳房胀痛</li><li>体重增加</li><li>情绪波动</li><li>恶心</li></ul></li><li><p><strong>抗雄激素相关</strong></p><ul><li>疲劳</li><li>性欲下降</li><li>电解质紊乱</li><li>肝功能异常</li></ul></li></ol><h3 id="应对策略" tabindex="-1">应对策略 <a class="header-anchor" href="#应对策略" aria-label="Permalink to &quot;应对策略&quot;">​</a></h3><ol><li><p><strong>症状管理</strong></p><ul><li>适度运动</li><li>调整饮食</li><li>充足休息</li><li>心理调适</li></ul></li><li><p><strong>就医指征</strong></p><ul><li>严重头痛</li><li>视觉异常</li><li>胸痛</li><li>呼吸困难</li><li>单侧肢体肿胀</li></ul></li></ol><h2 id="资源链接" tabindex="-1">资源链接 <a class="header-anchor" href="#资源链接" aria-label="Permalink to &quot;资源链接&quot;">​</a></h2><h3 id="医疗资源" tabindex="-1">医疗资源 <a class="header-anchor" href="#医疗资源" aria-label="Permalink to &quot;医疗资源&quot;">​</a></h3><ul><li><a href="/resources/doctors.html">友善医生名单</a></li><li><a href="/resources/hospitals.html">医院推荐</a></li><li><a href="/resources/tests.html">检查项目说明</a></li></ul><h3 id="知识资源" tabindex="-1">知识资源 <a class="header-anchor" href="#知识资源" aria-label="Permalink to &quot;知识资源&quot;">​</a></h3><ul><li><a href="/medical/hrt/overview.html">HRT 概述</a></li><li><a href="/medical/hrt/effects.html">副作用管理</a></li><li><a href="/medical/hrt/tests.html">激素检查解读</a></li></ul><div class="tip custom-block"><p class="custom-block-title">提醒</p><ol><li>所有药物都要在医生指导下使用</li><li>定期进行必要的检查</li><li>保持良好的生活习惯</li><li>有任何异常及时就医</li></ol></div><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><ol><li>WPATH SOC8 激素治疗指南</li><li>内分泌学会跨性别治疗指南</li><li>临床用药经验总结</li><li>药品说明书汇总</li></ol><h2 id="实用细节" tabindex="-1">实用细节 <a class="header-anchor" href="#实用细节" aria-label="Permalink to &quot;实用细节&quot;">​</a></h2><h3 id="服药时间建议" tabindex="-1">服药时间建议 <a class="header-anchor" href="#服药时间建议" aria-label="Permalink to &quot;服药时间建议&quot;">​</a></h3><ol><li><p><strong>雌二醇</strong></p><ul><li>建议分次服用，每次间隔8-12小时</li><li>可以选择早晚服用，如8:00和20:00</li><li>舌下含服时间建议10-15分钟</li><li>避免与食物同服，建议空腹</li></ul></li><li><p><strong>色普龙</strong></p><ul><li>建议早晚分次服用</li><li>可以与雌二醇同时服用</li><li>建议饭后服用以减少胃部不适</li><li>如出现嗜睡，可调整到晚上服用</li></ul></li><li><p><strong>螺内酯</strong></p><ul><li>建议早晚分次服用</li><li>注意可能增加排尿频率</li><li>避免睡前服用</li><li>建议饭后服用</li></ul></li></ol><h3 id="用药小技巧" tabindex="-1">用药小技巧 <a class="header-anchor" href="#用药小技巧" aria-label="Permalink to &quot;用药小技巧&quot;">​</a></h3><ol><li><p><strong>提高依从性</strong></p><ul><li>使用药盒整理每日用药</li><li>设置手机闹钟提醒</li><li>记录服药日志</li><li>准备便携药盒</li></ul></li><li><p><strong>储存建议</strong></p><ul><li>避免阳光直射</li><li>控制储存温度在25℃以下</li><li>保持干燥，避免潮湿</li><li>使用密封容器</li></ul></li><li><p><strong>应对漏服</strong></p><ul><li>记录漏服时间</li><li>不要补服过多剂量</li><li>继续下一次正常服用</li><li>保持剂量稳定</li></ul></li></ol><h2 id="常见问题解答" tabindex="-1">常见问题解答 <a class="header-anchor" href="#常见问题解答" aria-label="Permalink to &quot;常见问题解答&quot;">​</a></h2><h3 id="关于药物" tabindex="-1">关于药物 <a class="header-anchor" href="#关于药物" aria-label="Permalink to &quot;关于药物&quot;">​</a></h3><ol><li><p><strong>如何选择雌二醇剂型？</strong></p><ul><li>片剂：价格相对便宜，使用方便</li><li>凝胶：副作用较小，吸收稳定</li><li>贴片：使用方便，避免首过效应</li><li>建议根据个人情况和医生建议选择</li></ul></li><li><p><strong>如何处理服药后不适？</strong></p><ul><li>恶心：尝试饭后服用</li><li>嗜睡：调整服药时间</li><li>胃部不适：可能需要调整剂量</li><li>情绪波动：保持规律作息</li></ul></li><li><p><strong>如何判断药物效果？</strong></p><ul><li>观察身体变化</li><li>定期进行激素检查</li><li>记录变化日志</li><li>保持耐心，避免频繁调药</li></ul></li></ol><h3 id="关于检查" tabindex="-1">关于检查 <a class="header-anchor" href="#关于检查" aria-label="Permalink to &quot;关于检查&quot;">​</a></h3><ol><li><p><strong>需要检查哪些项目？</strong></p><ul><li>性激素六项</li><li>肝肾功能</li><li>电解质</li><li>凝血功能</li><li>血脂</li></ul></li><li><p><strong>检查频率如何安排？</strong></p><ul><li>开始用药前全面检查</li><li>用药1-3个月复查</li><li>稳定后每3-6个月复查</li><li>如有不适及时检查</li></ul></li><li><p><strong>如何解读检查结果？</strong></p><ul><li>关注激素水平变化</li><li>注意肝功能指标</li><li>监测电解质平衡</li><li>定期评估风险</li></ul></li></ol><h3 id="特殊情况" tabindex="-1">特殊情况 <a class="header-anchor" href="#特殊情况" aria-label="Permalink to &quot;特殊情况&quot;">​</a></h3><ol><li><p><strong>手术期间用药调整</strong></p><ul><li>遵医嘱停药时间</li><li>记录停药起止日期</li><li>术后按医嘱恢复</li><li>避免自行决定</li></ul></li><li><p><strong>感冒发烧期间</strong></p><ul><li>咨询医生是否需要停药</li><li>注意药物相互作用</li><li>记录身体状况</li><li>及时就医</li></ul></li><li><p><strong>旅行注意事项</strong></p><ul><li>准备足量药物</li><li>携带处方复印件</li><li>注意药物储存条件</li><li>了解当地医疗资源</li></ul></li></ol>',50)]))}const p=i(t,[["render",e]]);export{c as __pageData,p as default};
