import{_ as i,c as a,o as t,ae as o}from"./chunks/framework.D8jv0IBw.js";const g=JSON.parse('{"title":"HRT 激素检查解读","description":"","frontmatter":{},"headers":[],"relativePath":"medical/hrt/tests.md","filePath":"medical/hrt/tests.md","lastUpdated":null}'),r={name:"medical/hrt/tests.md"};function e(n,l,u,s,h,p){return t(),a("div",null,l[0]||(l[0]=[o('<h1 id="hrt-激素检查解读" tabindex="-1">HRT 激素检查解读 <a class="header-anchor" href="#hrt-激素检查解读" aria-label="Permalink to &quot;HRT 激素检查解读&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">提示</p><p>本文内容仅供参考。具体检查结果解读请以专业医生意见为准。</p></div><h2 id="基础检查项目" tabindex="-1">基础检查项目 <a class="header-anchor" href="#基础检查项目" aria-label="Permalink to &quot;基础检查项目&quot;">​</a></h2><h3 id="性激素六项" tabindex="-1">性激素六项 <a class="header-anchor" href="#性激素六项" aria-label="Permalink to &quot;性激素六项&quot;">​</a></h3><ol><li><p><strong>雌二醇 (E2)</strong></p><ul><li>参考范围：女性 100-400 pg/mL</li><li>目标范围：200-300 pg/mL</li><li>临床意义： <ul><li>反映雌激素水平</li><li>评估 HRT 效果</li><li>指导剂量调整</li></ul></li><li>注意事项： <ul><li>建议抽血前12小时禁食</li><li>避免剧烈运动</li><li>记录上次服药时间</li></ul></li></ul></li><li><p><strong>睾酮 (T)</strong></p><ul><li>参考范围：女性 &lt; 50 ng/dL</li><li>目标范围：20-50 ng/dL</li><li>临床意义： <ul><li>评估抗雄效果</li><li>指导抗雄药物调整</li><li>监测抑制程度</li></ul></li><li>注意事项： <ul><li>晨起空腹检查</li><li>避免运动后检查</li><li>记录用药情况</li></ul></li></ul></li><li><p><strong>泌乳素 (PRL)</strong></p><ul><li>参考范围：2-25 ng/mL</li><li>注意事项： <ul><li>过高需要关注</li><li>可能影响乳房发育</li><li>监测药物副作用</li></ul></li></ul></li><li><p><strong>促卵泡激素 (FSH)</strong></p><ul><li>参考范围：根据年龄段不同</li><li>临床意义： <ul><li>评估性腺功能</li><li>监测下丘脑功能</li><li>辅助诊断</li></ul></li></ul></li><li><p><strong>促黄体激素 (LH)</strong></p><ul><li>参考范围：根据年龄段不同</li><li>临床意义： <ul><li>评估性腺功能</li><li>监测药物效果</li><li>辅助诊断</li></ul></li></ul></li><li><p><strong>孕酮 (P)</strong></p><ul><li>参考范围：&lt; 1 ng/mL</li><li>临床意义： <ul><li>监测副作用</li><li>评估代谢情况</li></ul></li></ul></li></ol><h2 id="其他重要检查" tabindex="-1">其他重要检查 <a class="header-anchor" href="#其他重要检查" aria-label="Permalink to &quot;其他重要检查&quot;">​</a></h2><h3 id="肝功能" tabindex="-1">肝功能 <a class="header-anchor" href="#肝功能" aria-label="Permalink to &quot;肝功能&quot;">​</a></h3><ol><li><p><strong>转氨酶</strong></p><ul><li>ALT：&lt; 40 U/L</li><li>AST：&lt; 40 U/L</li><li>意义：评估肝功能</li></ul></li><li><p><strong>胆红素</strong></p><ul><li>总胆红素：&lt; 21 μmol/L</li><li>直接胆红素：&lt; 7 μmol/L</li><li>意义：评估肝脏代谢</li></ul></li><li><p><strong>γ-谷氨酰转肽酶</strong></p><ul><li>GGT：&lt; 50 U/L</li><li>意义：药物代谢指标</li></ul></li></ol><h3 id="肾功能" tabindex="-1">肾功能 <a class="header-anchor" href="#肾功能" aria-label="Permalink to &quot;肾功能&quot;">​</a></h3><ol><li><p><strong>肌酐</strong></p><ul><li>参考范围：44-133 μmol/L</li><li>意义：评估肾功能</li></ul></li><li><p><strong>尿素氮</strong></p><ul><li>参考范围：2.9-8.2 mmol/L</li><li>意义：评估代谢功能</li></ul></li></ol><h3 id="电解质" tabindex="-1">电解质 <a class="header-anchor" href="#电解质" aria-label="Permalink to &quot;电解质&quot;">​</a></h3><ol><li><p><strong>钾</strong></p><ul><li>参考范围：3.5-5.5 mmol/L</li><li>注意事项： <ul><li>使用螺内酯需监测</li><li>注意补钾情况</li></ul></li></ul></li><li><p><strong>钠</strong></p><ul><li>参考范围：135-145 mmol/L</li><li>意义：水电平衡</li></ul></li></ol><h3 id="血脂" tabindex="-1">血脂 <a class="header-anchor" href="#血脂" aria-label="Permalink to &quot;血脂&quot;">​</a></h3><ol><li><p><strong>总胆固醇</strong></p><ul><li>参考范围：&lt; 5.2 mmol/L</li><li>意义：心血管风险</li></ul></li><li><p><strong>甘油三酯</strong></p><ul><li>参考范围：&lt; 1.7 mmol/L</li><li>意义：代谢状况</li></ul></li></ol><h2 id="检查时间安排" tabindex="-1">检查时间安排 <a class="header-anchor" href="#检查时间安排" aria-label="Permalink to &quot;检查时间安排&quot;">​</a></h2><h3 id="初次检查" tabindex="-1">初次检查 <a class="header-anchor" href="#初次检查" aria-label="Permalink to &quot;初次检查&quot;">​</a></h3><ol><li><p><strong>用药前检查</strong></p><ul><li>全面基础检查</li><li>激素水平测定</li><li>凝血功能</li><li>血脂评估</li></ul></li><li><p><strong>准备事项</strong></p><ul><li>空腹8-12小时</li><li>避免剧烈运动</li><li>记录既往病史</li><li>准备身份证件</li></ul></li></ol><h3 id="随访检查" tabindex="-1">随访检查 <a class="header-anchor" href="#随访检查" aria-label="Permalink to &quot;随访检查&quot;">​</a></h3><ol><li><p><strong>首月复查</strong></p><ul><li>肝肾功能</li><li>电解质</li><li>激素水平</li></ul></li><li><p><strong>3个月复查</strong></p><ul><li>全面激素检查</li><li>肝肾功能</li><li>血脂检查</li><li>不良反应评估</li></ul></li><li><p><strong>长期监测</strong></p><ul><li>每3-6个月复查</li><li>定期全面评估</li><li>根据情况调整</li><li>记录变化趋势</li></ul></li></ol><h2 id="结果解读" tabindex="-1">结果解读 <a class="header-anchor" href="#结果解读" aria-label="Permalink to &quot;结果解读&quot;">​</a></h2><h3 id="常见异常" tabindex="-1">常见异常 <a class="header-anchor" href="#常见异常" aria-label="Permalink to &quot;常见异常&quot;">​</a></h3><ol><li><p><strong>雌二醇过高</strong></p><ul><li>可能原因： <ul><li>剂量过大</li><li>服药时间与采血时间过近</li><li>检测方法误差</li></ul></li><li>处理建议： <ul><li>调整剂量</li><li>规范服药时间</li><li>复查确认</li></ul></li></ul></li><li><p><strong>睾酮不达标</strong></p><ul><li>可能原因： <ul><li>抗雄剂量不足</li><li>药物吸收问题</li><li>个体差异</li></ul></li><li>处理建议： <ul><li>调整用药方案</li><li>更换药物种类</li><li>密切监测</li></ul></li></ul></li><li><p><strong>肝功能异常</strong></p><ul><li>可能原因： <ul><li>药物相关性损害</li><li>基础疾病</li><li>其他因素</li></ul></li><li>处理建议： <ul><li>及时就医</li><li>调整用药</li><li>保肝治疗</li></ul></li></ul></li></ol><h3 id="监测要点" tabindex="-1">监测要点 <a class="header-anchor" href="#监测要点" aria-label="Permalink to &quot;监测要点&quot;">​</a></h3><ol><li><p><strong>趋势分析</strong></p><ul><li>记录历次结果</li><li>对比变化趋势</li><li>评估治疗效果</li><li>预测潜在风险</li></ul></li><li><p><strong>综合评估</strong></p><ul><li>结合临床症状</li><li>考虑用药情况</li><li>评估生活方式</li><li>个体化分析</li></ul></li></ol><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h2><h3 id="检查准备" tabindex="-1">检查准备 <a class="header-anchor" href="#检查准备" aria-label="Permalink to &quot;检查准备&quot;">​</a></h3><ol><li><p><strong>生活作息</strong></p><ul><li>规律作息</li><li>避免熬夜</li><li>适量运动</li><li>清淡饮食</li></ul></li><li><p><strong>用药管理</strong></p><ul><li>记录服药时间</li><li>遵医嘱用药</li><li>避免擅自调药</li><li>保存检查记录</li></ul></li></ol><h3 id="结果管理" tabindex="-1">结果管理 <a class="header-anchor" href="#结果管理" aria-label="Permalink to &quot;结果管理&quot;">​</a></h3><ol><li><p><strong>建立档案</strong></p><ul><li>保存报告单</li><li>记录异常项</li><li>整理就医记录</li><li>记录症状变化</li></ul></li><li><p><strong>沟通反馈</strong></p><ul><li>及时咨询医生</li><li>如实报告情况</li><li>保持随访联系</li><li>遵医嘱调整</li></ul></li></ol><div class="tip custom-block"><p class="custom-block-title">提醒</p><ol><li>检查结果要结合临床症状综合分析</li><li>定期检查是保证用药安全的重要手段</li><li>发现异常及时就医</li><li>保持良好的记录习惯</li></ol></div><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><ol><li>内分泌学会检验指南</li><li>WPATH SOC8 监测章节</li><li>临床检验参考范围</li><li>激素治疗随访经验总结</li></ol>',32)]))}const c=i(r,[["render",e]]);export{g as __pageData,c as default};
