import{_ as i,c as a,o,ae as r}from"./chunks/framework.D8jv0IBw.js";const g=JSON.parse('{"title":"SRS 术后护理指南","description":"","frontmatter":{},"headers":[],"relativePath":"medical/srs/aftercare.md","filePath":"medical/srs/aftercare.md","lastUpdated":null}'),t={name:"medical/srs/aftercare.md"};function e(s,l,n,u,h,p){return o(),a("div",null,l[0]||(l[0]=[r('<h1 id="srs-术后护理指南" tabindex="-1">SRS 术后护理指南 <a class="header-anchor" href="#srs-术后护理指南" aria-label="Permalink to &quot;SRS 术后护理指南&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">提示</p><p>本文内容仅供参考。具体护理工作请遵医嘱进行。</p></div><h2 id="住院期护理" tabindex="-1">住院期护理 <a class="header-anchor" href="#住院期护理" aria-label="Permalink to &quot;住院期护理&quot;">​</a></h2><h3 id="早期护理-1-3天" tabindex="-1">早期护理（1-3天） <a class="header-anchor" href="#早期护理-1-3天" aria-label="Permalink to &quot;早期护理（1-3天）&quot;">​</a></h3><ol><li><p><strong>基础护理</strong></p><ul><li>严格卧床休息</li><li>保持伤口清洁</li><li>观察引流情况</li><li>记录排尿情况</li><li>监测生命体征</li></ul></li><li><p><strong>疼痛管理</strong></p><ul><li>按时服用止痛药</li><li>记录疼痛程度</li><li>避免剧烈动作</li><li>保持适当体位</li></ul></li><li><p><strong>饮食护理</strong></p><ul><li>流质饮食起始</li><li>少量多次</li><li>避免刺激性食物</li><li>保持充分水分</li></ul></li></ol><h3 id="中期护理-4-7天" tabindex="-1">中期护理（4-7天） <a class="header-anchor" href="#中期护理-4-7天" aria-label="Permalink to &quot;中期护理（4-7天）&quot;">​</a></h3><ol><li><p><strong>活动护理</strong></p><ul><li>适当床上活动</li><li>循序渐进下床</li><li>避免剧烈运动</li><li>保持正确姿势</li></ul></li><li><p><strong>伤口护理</strong></p><ul><li>定期换药</li><li>观察愈合情况</li><li>保持清洁干燥</li><li>预防感染</li></ul></li><li><p><strong>功能锻炼</strong></p><ul><li>膀胱功能训练</li><li>盆底肌锻炼</li><li>呼吸功能锻炼</li><li>下肢活动</li></ul></li></ol><h3 id="后期护理-8-14天" tabindex="-1">后期护理（8-14天） <a class="header-anchor" href="#后期护理-8-14天" aria-label="Permalink to &quot;后期护理（8-14天）&quot;">​</a></h3><ol><li><p><strong>活动增加</strong></p><ul><li>逐步增加活动量</li><li>学习正确走路</li><li>避免久坐久站</li><li>保持适度运动</li></ul></li><li><p><strong>伤口观察</strong></p><ul><li>继续换药护理</li><li>观察愈合进展</li><li>注意异常情况</li><li>保持局部卫生</li></ul></li><li><p><strong>生活适应</strong></p><ul><li>学习自我护理</li><li>掌握基本技能</li><li>建立生活规律</li><li>心理适应</li></ul></li></ol><h2 id="出院后护理" tabindex="-1">出院后护理 <a class="header-anchor" href="#出院后护理" aria-label="Permalink to &quot;出院后护理&quot;">​</a></h2><h3 id="早期-1-4周" tabindex="-1">早期（1-4周） <a class="header-anchor" href="#早期-1-4周" aria-label="Permalink to &quot;早期（1-4周）&quot;">​</a></h3><ol><li><p><strong>基本护理</strong></p><ul><li>保持清洁卫生</li><li>定期更换敷料</li><li>避免剧烈运动</li><li>规律作息</li></ul></li><li><p><strong>扩张护理</strong></p><ul><li>遵医嘱进行扩张</li><li>记录扩张情况</li><li>保持器具清洁</li><li>注意疼痛反应</li></ul></li><li><p><strong>饮食调养</strong></p><ul><li>营养均衡</li><li>高蛋白饮食</li><li>补充维生素</li><li>适量水分</li></ul></li></ol><h3 id="中期-1-3月" tabindex="-1">中期（1-3月） <a class="header-anchor" href="#中期-1-3月" aria-label="Permalink to &quot;中期（1-3月）&quot;">​</a></h3><ol><li><p><strong>日常护理</strong></p><ul><li>保持会阴清洁</li><li>合理安排扩张</li><li>观察分泌物</li><li>适度活动</li></ul></li><li><p><strong>功能恢复</strong></p><ul><li>盆底功能锻炼</li><li>膀胱功能训练</li><li>性功能适应</li><li>心理调适</li></ul></li><li><p><strong>并发症预防</strong></p><ul><li>观察感染征象</li><li>预防粘连</li><li>避免外伤</li><li>定期复查</li></ul></li></ol><h3 id="长期护理-3月以后" tabindex="-1">长期护理（3月以后） <a class="header-anchor" href="#长期护理-3月以后" aria-label="Permalink to &quot;长期护理（3月以后）&quot;">​</a></h3><ol><li><p><strong>日常维护</strong></p><ul><li>保持个人卫生</li><li>定期扩张维持</li><li>适度运动</li><li>规律生活</li></ul></li><li><p><strong>功能维护</strong></p><ul><li>维持扩张频率</li><li>保持功能锻炼</li><li>关注身体变化</li><li>定期医疗随访</li></ul></li><li><p><strong>生活适应</strong></p><ul><li>逐步恢复工作</li><li>社会活动参与</li><li>心理健康维护</li><li>建立支持网络</li></ul></li></ol><h2 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h2><h3 id="清洁护理" tabindex="-1">清洁护理 <a class="header-anchor" href="#清洁护理" aria-label="Permalink to &quot;清洁护理&quot;">​</a></h3><ol><li><p><strong>会阴护理</strong></p><ul><li>每日清洗2-3次</li><li>使用温水冲洗</li><li>由前向后擦拭</li><li>保持干燥</li></ul></li><li><p><strong>器具护理</strong></p><ul><li>扩张器消毒</li><li>定期更换</li><li>使用前检查</li><li>保持清洁</li></ul></li><li><p><strong>环境卫生</strong></p><ul><li>保持床单清洁</li><li>勤换内裤</li><li>通风换气</li><li>避免潮湿</li></ul></li></ol><h3 id="活动注意" tabindex="-1">活动注意 <a class="header-anchor" href="#活动注意" aria-label="Permalink to &quot;活动注意&quot;">​</a></h3><ol><li><p><strong>活动限制</strong></p><ul><li>避免剧烈运动</li><li>避免重物提举</li><li>避免长时间坐立</li><li>循序渐进恢复</li></ul></li><li><p><strong>姿势要求</strong></p><ul><li>保持正确体位</li><li>避免压迫伤口</li><li>适当休息</li><li>定时活动</li></ul></li><li><p><strong>运动建议</strong></p><ul><li>适度散步</li><li>轻柔伸展</li><li>盆底运动</li><li>渐进性增加</li></ul></li></ol><h3 id="异常处理" tabindex="-1">异常处理 <a class="header-anchor" href="#异常处理" aria-label="Permalink to &quot;异常处理&quot;">​</a></h3><ol><li><p><strong>需要就医情况</strong></p><ul><li>发热</li><li>剧烈疼痛</li><li>大量出血</li><li>异常分泌物</li><li>伤口感染征象</li></ul></li><li><p><strong>自我观察</strong></p><ul><li>每日体温监测</li><li>伤口愈合情况</li><li>分泌物性状</li><li>排尿情况</li></ul></li><li><p><strong>预防措施</strong></p><ul><li>保持充足休息</li><li>避免感染源</li><li>规律作息</li><li>心理调适</li></ul></li></ol><h2 id="生活指导" tabindex="-1">生活指导 <a class="header-anchor" href="#生活指导" aria-label="Permalink to &quot;生活指导&quot;">​</a></h2><h3 id="饮食建议" tabindex="-1">饮食建议 <a class="header-anchor" href="#饮食建议" aria-label="Permalink to &quot;饮食建议&quot;">​</a></h3><ol><li><p><strong>饮食原则</strong></p><ul><li>营养均衡</li><li>易消化</li><li>高蛋白</li><li>富含维生素</li></ul></li><li><p><strong>禁忌食物</strong></p><ul><li>刺激性食物</li><li>酒精</li><li>咖啡因</li><li>重油重盐</li></ul></li><li><p><strong>补充建议</strong></p><ul><li>蛋白质</li><li>维生素C</li><li>铁剂</li><li>钙片</li></ul></li></ol><h3 id="心理调适" tabindex="-1">心理调适 <a class="header-anchor" href="#心理调适" aria-label="Permalink to &quot;心理调适&quot;">​</a></h3><ol><li><p><strong>情绪管理</strong></p><ul><li>保持乐观</li><li>接纳变化</li><li>寻求支持</li><li>适度表达</li></ul></li><li><p><strong>社会支持</strong></p><ul><li>家人陪伴</li><li>朋友交流</li><li>同伴支持</li><li>专业咨询</li></ul></li><li><p><strong>生活重建</strong></p><ul><li>逐步适应</li><li>建立信心</li><li>社会融入</li><li>职业规划</li></ul></li></ol><div class="tip custom-block"><p class="custom-block-title">提醒</p><ol><li>严格遵医嘱进行护理</li><li>保持耐心，恢复是漫长的过程</li><li>出现异常及时就医</li><li>保持良好的心态</li></ol></div><h2 id="参考资料" tabindex="-1">参考资料 <a class="header-anchor" href="#参考资料" aria-label="Permalink to &quot;参考资料&quot;">​</a></h2><ol><li>WPATH SOC8 术后护理章节</li><li>手术后护理指南</li><li>临床护理经验总结</li><li>康复指导手册</li></ol>',31)]))}const c=i(t,[["render",e]]);export{g as __pageData,c as default};
