import{_ as i,c as a,o as t,ae as o}from"./chunks/framework.D8jv0IBw.js";const p=JSON.parse('{"title":"HRT 知情同意门诊","description":"","frontmatter":{"title":"HRT 知情同意门诊"},"headers":[],"relativePath":"resources/hrt-clinics.md","filePath":"resources/hrt-clinics.md","lastUpdated":null}'),e={name:"resources/hrt-clinics.md"};function u(s,l,r,c,h,n){return t(),a("div",null,l[0]||(l[0]=[o('<h1 id="hrt-知情同意门诊" tabindex="-1">HRT 知情同意门诊 <a class="header-anchor" href="#hrt-知情同意门诊" aria-label="Permalink to &quot;HRT 知情同意门诊&quot;">​</a></h1><h2 id="什么是知情同意模式" tabindex="-1">什么是知情同意模式 <a class="header-anchor" href="#什么是知情同意模式" aria-label="Permalink to &quot;什么是知情同意模式&quot;">​</a></h2><p>知情同意模式是一种基于患者自主权的医疗服务模式。在 HRT 治疗中，医生会：</p><ol><li>详细解释治疗的效果和风险</li><li>确保患者理解并接受这些信息</li><li>尊重患者的治疗决定</li></ol><h2 id="全国门诊列表" tabindex="-1">全国门诊列表 <a class="header-anchor" href="#全国门诊列表" aria-label="Permalink to &quot;全国门诊列表&quot;">​</a></h2><div class="warning custom-block"><p class="custom-block-title">注意</p><p>以下信息仅供参考，具体情况请联系医院确认。 如发现信息有误，请及时反馈更新。</p></div><h3 id="北京" tabindex="-1">北京 <a class="header-anchor" href="#北京" aria-label="Permalink to &quot;北京&quot;">​</a></h3><ul><li><p>北京大学第三医院（Peking University Third Hospital，PUTH）</p><ul><li>地址：北京市海淀区花园北路 49 号</li><li>科室： <ul><li>内分泌科：刘烨医生 <ul><li>周二晚间：内分泌科晚间门诊(特需)，健康医学楼七层</li><li>周四下午：内分泌门诊-易性症激素治疗门诊，门诊楼三层内分泌专家诊区</li></ul></li><li>成形外科：潘柏林医生 <ul><li>周二晚间：成形外科晚间门诊(特需)，健康医学楼七层18诊室</li></ul></li></ul></li><li>挂号方式： <ul><li>微信挂号：通过以下三种方式之一 <ol><li>北医三院 APP</li><li>「北医三院服务号」微信公众号</li><li>北京市预约挂号统一平台（114平台）</li></ol></li><li>注意：前两种方式号源共享，114平台号源独立</li></ul></li><li>所需材料： <ul><li>身份证原件</li><li>就诊卡/医保卡（如有）</li><li>既往病历资料（如有）</li><li>监护人（未满18岁必需）</li></ul></li><li>注意事项： <ul><li>初诊建议挂刘烨医生的普通门诊（周四下午）</li><li>复诊可以根据需要选择特需门诊</li><li>建议提前1-2周预约挂号</li><li>就诊前准备好详细的问题清单</li><li>如实告知既往病史和用药情况</li></ul></li></ul><div class="warning custom-block"><p class="custom-block-title">年龄相关要求</p><ul><li>16岁及以下：不能启动激素治疗，可考虑使用青春期阻滞剂</li><li>16-18岁：需至少一位父母陪同就诊并现场签字</li><li>18岁以上：建议至少一位家长知情且不反对，如与家长意见相左，原则上尊重就诊者本人意愿</li></ul></div><div class="tip custom-block"><p class="custom-block-title">就医流程</p><ol><li>通过上述方式预约挂号</li><li>就诊当天提前半小时到达医院</li><li>持身份证到自助机或窗口建卡/缴费</li><li>按时到达指定诊室就诊</li><li>遵医嘱进行必要的检查</li><li>根据检查结果制定治疗方案</li></ol></div></li></ul><h3 id="上海" tabindex="-1">上海 <a class="header-anchor" href="#上海" aria-label="Permalink to &quot;上海&quot;">​</a></h3><ul><li><p>上海精神卫生中心（上海市精神卫生中心，Shanghai Mental Health Center，SMHC）</p><ul><li>地址：上海市徐汇区宛平南路600号</li><li>科室： <ul><li>心理咨询科/性心理科 <ul><li>门诊楼二楼</li></ul></li></ul></li><li>挂号方式： <ul><li>线上预约： <ol><li>上海市预约挂号平台（114）</li><li>医院官网</li><li>复诊可使用&quot;上海精卫&quot;APP</li></ol></li><li>现场挂号：医院挂号大厅</li></ul></li><li>所需材料： <ul><li>身份证原件</li><li>就诊卡/医保卡（如有）</li><li>既往病历资料（如有）</li><li>监护人（未满18岁必需）</li></ul></li><li>注意事项： <ul><li>建议提前通过114平台预约挂号</li><li>初诊建议挂普通门诊</li><li>需要经过心理科评估</li><li>如实告知既往病史和用药情况</li></ul></li></ul><div class="warning custom-block"><p class="custom-block-title">就医提示</p><ul><li>该医院采用分诊制，需要先进行心理评估</li><li>可能需要多次就诊才能开具处方</li><li>建议携带既往就医记录（如有）</li></ul></div><div class="tip custom-block"><p class="custom-block-title">就医流程</p><ol><li>预约挂号（建议提前1-2周）</li><li>就诊当天提前到达医院</li><li>进行心理评估</li><li>根据评估结果进行后续治疗</li><li>遵医嘱进行必要的检查</li><li>制定治疗方案</li></ol></div></li><li><p>上海第九人民医院（Shanghai Ninth People&#39;s Hospital）</p><ul><li>地址：上海市黄浦区制造局路639号</li><li>科室： <ul><li>内分泌科</li><li>整形外科</li></ul></li><li>挂号方式： <ul><li>线上预约： <ol><li>上海市预约挂号平台（114）</li><li>医院官网</li><li>九院APP</li></ol></li></ul></li><li>所需材料： <ul><li>身份证原件</li><li>就诊卡/医保卡（如有）</li><li>既往病历资料（如有）</li><li>监护人（未满18岁必需）</li></ul></li><li>注意事项： <ul><li>号源紧张，建议提前预约</li><li>需要经过专科医生评估</li><li>如实告知既往病史和用药情况</li></ul></li></ul><div class="warning custom-block"><p class="custom-block-title">重要提醒</p><ul><li>建议先在精神卫生中心进行评估</li><li>携带相关评估证明可能有助于就医</li><li>准备好详细的病史和主诉</li></ul></div></li></ul><h3 id="济南" tabindex="-1">济南 <a class="header-anchor" href="#济南" aria-label="Permalink to &quot;济南&quot;">​</a></h3><ul><li><p>山东省立医院（Shandong Provincial Hospital）</p><ul><li>地址：济南市槐荫区经十路 9677 号</li><li>科室： <ul><li>内分泌科 <ul><li>门诊楼三楼内分泌科</li></ul></li></ul></li><li>挂号方式： <ul><li>线上预约： <ol><li>山东省预约挂号平台</li><li>医院官网</li><li>&quot;山东省立医院&quot;APP</li></ol></li><li>现场挂号：医院挂号大厅</li></ul></li><li>所需材料： <ul><li>身份证原件</li><li>就诊卡/医保卡（如有）</li><li>既往病历资料（如有）</li><li>监护人（未满18岁必需）</li></ul></li><li>注意事项： <ul><li>建议提前预约挂号</li><li>初诊建议挂普通门诊</li><li>如实告知既往病史和用药情况</li></ul></li></ul><div class="warning custom-block"><p class="custom-block-title">就医提示</p><ul><li>建议携带既往就医记录（如有）</li><li>准备好详细的问诊内容</li><li>可能需要进行相关检查</li></ul></div><div class="tip custom-block"><p class="custom-block-title">就医流程</p><ol><li>预约挂号</li><li>就诊当天提前到达医院</li><li>进行初诊评估</li><li>遵医嘱进行必要的检查</li><li>根据检查结果制定治疗方案</li></ol></div></li></ul><h2 id="就医准备" tabindex="-1">就医准备 <a class="header-anchor" href="#就医准备" aria-label="Permalink to &quot;就医准备&quot;">​</a></h2><h3 id="所需材料" tabindex="-1">所需材料 <a class="header-anchor" href="#所需材料" aria-label="Permalink to &quot;所需材料&quot;">​</a></h3><ul><li>身份证</li><li>病历本</li><li>既往病史资料</li><li>心理评估报告（如需）</li><li>监护人陪同（如需）</li></ul><h3 id="注意事项" tabindex="-1">注意事项 <a class="header-anchor" href="#注意事项" aria-label="Permalink to &quot;注意事项&quot;">​</a></h3><ol><li>提前预约</li><li>准备详细的问题清单</li><li>如实告知病史</li><li>保存所有检查报告</li></ol><h2 id="相关资源" tabindex="-1">相关资源 <a class="header-anchor" href="#相关资源" aria-label="Permalink to &quot;相关资源&quot;">​</a></h2><ul><li><a href="/mtfwiki/medical/hrt/overview.html">HRT 概述</a></li><li><a href="/mtfwiki/medical/hrt/guide.html">用药指南</a></li><li><a href="/mtfwiki/resources/hospitals.html">医院列表</a></li></ul>',19)]))}const m=i(e,[["render",u]]);export{p as __pageData,m as default};
